int main(void) {
    // Wait until the microcontroller booted up to remove increased power consumption in the measurements at the beginning.
    delay(3000);

    uint8_t dest[sizeof(uncompressed)] = {0};
    uint32_t dest_len = sizeof(dest);

    setup_pin();
    pin_off();

    int e = 0;

    // Test inflate.
    for (size_t i = 0; i < SAMPLES; ++i) {
        pin_on();
        for (size_t j = 0; j < 100; j++) {
            e = tinf_uncompress(
                dest,
                &dest_len,
                source,
                sizeof(source)
            );
        }
        pin_off();
        delay(500);
    }

    delay(1000);
    pin_on();
    for (size_t i = 0; i < SAMPLES; ++i) {
        for (size_t j = 0; j < 100; j++) {
            e = tinf_uncompress(
                dest,
                &dest_len,
                source,
                sizeof(source)
            );
        }
    }
    pin_off();

    return e;
}
