int main(void) {
    // Wait until the microcontroller booted up to remove increased power consumption in the measurements at the beginning.
    delay(3000);

    uint8_t hash[64] = {0};

    setup_pin();
    pin_off();

    int e = 0;

    // Test SHA512.
    for (size_t i = 0; i < SAMPLES; ++i) {
        pin_on();
        fido_sha512(
            data,
            sizeof(data),
            hash
        );
        pin_off();
        delay(500);
    }

    delay(1000);
    pin_on();
    for (size_t i = 0; i < SAMPLES; ++i) {
        fido_sha512(
            data,
            sizeof(data),
            hash
        );
    }
    pin_off();

    return e;
}
